import React, { useState, useEffect } from 'react';

function Signin(){

	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [isSubmitted, setIsSubmitted] = useState(false);

	const handleEmailOnChange = (event) => setEmail(event.target.value);
	const handlePasswordOnChange = (event) => setPassword(event.target.value);

	const handleDetailsSubmit = () => {
		console.log('>>> email is ' + email + ' password is ' + password);
		setIsSubmitted(true);
	}

	useEffect(() => {
		if (isSubmitted) {
			const verifyUserCredentials = async () => {
				let options = {
					method: 'POST',
					headers: {
						'Content-Type': 'application/json'
					},
					body: JSON.stringify({
						email: email,
						password: password
					})
				};

				await fetch('http://localhost:8080/api/verify-user-credentials', options);
			};

			verifyUserCredentials();
		}
		// eslint-disable-next-line
	}, [isSubmitted]);

	return(
		<div className="signin-container">
		  <input
		    className="signin-input"
		    type="text"
		    onChange={handleEmailOnChange}
		    value={email}
		    placeholder="Enter your Email"
		  />
		  <input
		    className="signin-input"
		    type="password"
		    onChange={handlePasswordOnChange}
		    value={password}
		    placeholder="Enter your Password"
		  />
		  <button
		    className="signin-button"
		    type="submit"
		    onClick={handleDetailsSubmit}
		  >
		    Sign In
		  </button>
		  <div className="signin-footer">
		    Don't have an account? <a href="http://localhost:3000/signup">Sign Up</a>
		  </div>
		</div>

	);
}

export default Signin;
