import React, { useState, useEffect } from 'react';
import '../css/Signup.css';

function Signup() {

	const [firstname, setFirstname] = useState('');
	const [lastname, setLastname] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');
	const [confirmPassword, setConfirmPassword] = useState('');
	const [isSubmitted, setIsSubmitted] = useState(false);

	const handleFirstnameOnChange = (event) => setFirstname(event.target.value);
	const handleLastnameOnChange  = (event) => setLastname(event.target.value);
	const handleEmailOnChange = (event) => setEmail(event.target.value);
	const handlePasswordOnChange = (event) => setPassword(event.target.value);
	const handleConfirmPasswordOnChange = (event) => setConfirmPassword(event.target.value);

	const verifyPassword = () => {
		return password === confirmPassword;
	}

	const handleDetailsSubmit = () => {
		console.log('>>> firstname is ', firstname, ' lastname is ', lastname);
		console.log('>>> email is ', email, ' password is ', password);
		if(!verifyPassword){
			alert('Password and Re-entered passwords do not match');
		}
		else{
			setIsSubmitted(true);
		}

	}

	useEffect(() => {
		if (isSubmitted) {
			const insertUser = async () => {
				let options = {
					method: 'POST',
					headers: {
						'Content-Type': 'application/json'
					},
					body: JSON.stringify({
						firstname: firstname,
						lastname: lastname,
						email: email,
						password: password
					})
				};

				await fetch('http://localhost:8080/api/insert-user', options);
			};

			insertUser();
		}
		// eslint-disable-next-line
	}, [isSubmitted]);

	return (

		<div className="signup-container">
			<input
				className="signup-input"
				type="text"
				onChange={handleFirstnameOnChange}
				value={firstname}
				placeholder="Enter your Firstname"
			/>
			<input
				className="signup-input"
				type="text"
				onChange={handleLastnameOnChange}
				value={lastname}
				placeholder="Enter your Lastname"
			/>
			<input
				className="signup-input"
				type="text"
				onChange={handleEmailOnChange}
				value={email}
				placeholder="Enter your Email"
			/>
			<input
				className="signup-input"
				type="text"
				onChange={handlePasswordOnChange}
				value={password}
				placeholder="Enter your Password"
			/>
			<input
				className="signup-input"
				type="text"
				onChange={handleConfirmPasswordOnChange}
				value={confirmPassword}
				placeholder="ReEnter your password"
			/>
			<button
				className="signup-button"
				type="submit"
				onClick={handleDetailsSubmit}
			>
				Submit
			</button>
			<div>
				Already have an account? <a href="http://localhost:3000/signin">Sign In</a>
			</div>
		</div>
	);

}

export default Signup;
